/**
 * TraceAuditPanel - TRACE (Tool-Routed Architecture for Controlled Execution) UI
 *
 * Renders the deterministic audit log with clear visual separation of:
 * 1. Audit Log (shield icon) â€” "These steps were executed deterministically"
 * 2. Raw Data (database icon) â€” "This data was produced by deterministic tools"
 * 3. AI Analysis (brain icon, badge) â€” "Generated by AI, may contain inaccuracies"
 *
 * Trust comes from the clear separation: users can always verify the audit log
 * and raw data, which are code-generated and deterministic.
 */

import React, { useState } from "react";
import { useQuery } from "convex/react";
import { api } from "../../../../../convex/_generated/api";
import {
  Shield,
  Database,
  Brain,
  ChevronDown,
  ChevronRight,
  CheckCircle2,
  XCircle,
  AlertTriangle,
  Clock,
  Search,
  Wrench,
  FileOutput,
  Flag,
  RefreshCw,
  Zap,
  Info,
} from "lucide-react";
import { cn } from "@/lib/utils";

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// TYPES
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface TraceAuditPanelProps {
  executionId: string;
  className?: string;
}

interface AuditEntry {
  _id: string;
  seq: number;
  timestamp: number;
  choiceType: "gather_info" | "execute_data_op" | "execute_output" | "finalize";
  toolName: string;
  toolParams?: any;
  metadata: {
    rowCount?: number;
    columnCount?: number;
    uniqueValues?: any;
    charCount?: number;
    wordCount?: number;
    keyTopics?: string[];
    errorMessage?: string;
    durationMs: number;
    success: boolean;
    intendedState?: string;
    actualState?: string;
    correctionApplied?: boolean;
  };
  description: string;
  createdAt: number;
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// HELPER FUNCTIONS
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

function getChoiceIcon(choiceType: AuditEntry["choiceType"]) {
  switch (choiceType) {
    case "gather_info":
      return <Search className="w-3.5 h-3.5 text-violet-500 dark:text-violet-400" />;
    case "execute_data_op":
      return <Wrench className="w-3.5 h-3.5 text-[var(--text-secondary)]" />;
    case "execute_output":
      return <FileOutput className="w-3.5 h-3.5 text-[var(--text-secondary)]" />;
    case "finalize":
      return <Flag className="w-3.5 h-3.5 text-[var(--text-secondary)]" />;
  }
}

function getChoiceLabel(choiceType: AuditEntry["choiceType"]): string {
  switch (choiceType) {
    case "gather_info":
      return "Gather";
    case "execute_data_op":
      return "Data Op";
    case "execute_output":
      return "Output";
    case "finalize":
      return "Finalize";
  }
}

function getChoiceBadgeColor(choiceType: AuditEntry["choiceType"]): string {
  switch (choiceType) {
    case "gather_info":
      return "bg-blue-100 text-blue-700 dark:bg-blue-900/30 dark:text-blue-400";
    case "execute_data_op":
      return "bg-[var(--bg-hover)] text-[var(--text-secondary)] dark:bg-[var(--bg-secondary)] dark:text-[var(--text-muted)]";
    case "execute_output":
      return "bg-purple-100 text-purple-700 dark:bg-purple-900/20 dark:text-purple-400";
    case "finalize":
      return "bg-green-100 text-green-700 dark:bg-green-900/20 dark:text-green-400";
  }
}

function formatDuration(ms: number): string {
  if (ms < 1000) return `${ms}ms`;
  return `${(ms / 1000).toFixed(1)}s`;
}

function formatTime(timestamp: number): string {
  return new Date(timestamp).toLocaleTimeString("en-US", {
    hour: "2-digit",
    minute: "2-digit",
    second: "2-digit",
    hour12: false,
  });
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// MAIN COMPONENT
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

export function TraceAuditPanel({ executionId, className }: TraceAuditPanelProps) {
  const [isExpanded, setIsExpanded] = useState(true);
  const [showDetails, setShowDetails] = useState<Set<number>>(new Set());

  // Fetch audit log and summary from Convex
  const auditEntries = useQuery(api.domains.agents.traceAuditLog.getAuditLog, {
    executionId,
  });
  const auditSummary = useQuery(api.domains.agents.traceAuditLog.getAuditSummary, {
    executionId,
  });

  if (!auditEntries || auditEntries.length === 0) {
    return null;
  }

  const toggleDetail = (seq: number) => {
    setShowDetails((prev) => {
      const next = new Set(prev);
      if (next.has(seq)) {
        next.delete(seq);
      } else {
        next.add(seq);
      }
      return next;
    });
  };

  const selfCorrections = auditEntries.filter(
    (e) => e.metadata.correctionApplied
  );

  return (
    <div className={cn("space-y-2", className)}>
      {/* â”€â”€ Section 1: Audit Log â”€â”€ */}
      <div className="border border-[var(--border-color)] rounded-lg bg-[var(--bg-secondary)]">
        {/* Header */}
        <button
          onClick={() => setIsExpanded(!isExpanded)}
          className="w-full flex items-center justify-between px-3 py-2 hover:bg-[var(--bg-tertiary)] transition-colors rounded-t-lg"
        >
          <div className="flex items-center gap-2">
            {isExpanded ? (
              <ChevronDown className="w-3.5 h-3.5 text-[var(--text-muted)]" />
            ) : (
              <ChevronRight className="w-3.5 h-3.5 text-[var(--text-muted)]" />
            )}
            <Shield className="w-3.5 h-3.5 text-green-500" />
            <span className="text-xs font-medium text-[var(--text-primary)]">
              Execution Trace
            </span>
            <span className="text-[10px] px-1.5 py-0.5 rounded-full bg-green-100 text-green-700 dark:bg-green-900/20 dark:text-green-400 font-medium">
              DETERMINISTIC
            </span>
          </div>

          {/* Quick Stats */}
          <div className="flex items-center gap-2.5 text-[11px] text-[var(--text-muted)]">
            {auditSummary && (
              <>
                <span>{auditSummary.totalSteps} steps</span>
                <span className="text-[var(--text-muted)]">Â·</span>
                <span>{formatDuration(auditSummary.totalDurationMs)}</span>
                {auditSummary.selfCorrections > 0 && (
                  <>
                    <span className="text-[var(--text-muted)]">Â·</span>
                    <span className="flex items-center gap-0.5">
                      <RefreshCw className="w-3 h-3" />
                      {auditSummary.selfCorrections}
                    </span>
                  </>
                )}
                {auditSummary.failures > 0 && (
                  <span className="flex items-center gap-0.5 text-red-500 dark:text-red-400">
                    <XCircle className="w-3 h-3" />
                    {auditSummary.failures}
                  </span>
                )}
                {auditSummary.hasFinalized && (
                  <CheckCircle2 className="w-3.5 h-3.5 text-green-500" />
                )}
              </>
            )}
          </div>
        </button>

        {/* Expanded Content */}
        {isExpanded && (
          <div className="border-t border-[var(--border-color)]">
            {/* Trust Banner */}
            <div className="px-3 py-1.5 bg-[var(--bg-primary)] text-[11px] text-[var(--text-muted)] flex items-center gap-2 border-b border-[var(--border-color)]">
              <Info className="w-3 h-3 flex-shrink-0" />
              <span>
                Steps executed by deterministic code â€” not generated by AI.
              </span>
            </div>

            {/* Entry List */}
            <div className="max-h-80 overflow-y-auto divide-y divide-[var(--border-color)]">
              {auditEntries.map((entry) => (
                <div key={entry._id}>
                  {/* Entry Row */}
                  <button
                    onClick={() => toggleDetail(entry.seq)}
                    className="w-full flex items-start gap-2 px-3 py-2 text-xs text-left hover:bg-[var(--bg-tertiary)] transition-colors"
                  >
                    {/* Sequence Number */}
                    <span className="text-[var(--text-muted)] font-mono w-4 flex-shrink-0 text-right tabular-nums">
                      {entry.seq + 1}.
                    </span>

                    {/* Choice Icon */}
                    <span className="flex-shrink-0 mt-0.5">
                      {getChoiceIcon(entry.choiceType)}
                    </span>

                    {/* Content */}
                    <div className="flex-1 min-w-0">
                      <div className="flex items-center gap-1.5 flex-wrap">
                        <span
                          className={cn(
                            "px-1.5 py-0.5 rounded text-[10px] font-medium leading-none",
                            getChoiceBadgeColor(entry.choiceType)
                          )}
                        >
                          {getChoiceLabel(entry.choiceType)}
                        </span>
                        <span className="text-[var(--text-primary)] font-medium truncate text-[11px]">
                          {entry.toolName}
                        </span>
                        {entry.metadata.correctionApplied && (
                          <span className="inline-flex items-center gap-0.5 text-[10px] text-amber-600 dark:text-amber-400">
                            <RefreshCw className="w-2.5 h-2.5" />
                            corrected
                          </span>
                        )}
                        {!entry.metadata.success && (
                          <XCircle className="w-3 h-3 text-red-500 dark:text-red-400" />
                        )}
                      </div>
                      <div className="text-[var(--text-muted)] mt-0.5 line-clamp-2 text-[11px] leading-relaxed">
                        {entry.description}
                      </div>
                    </div>

                    {/* Duration */}
                    <span className="text-[var(--text-muted)] font-mono text-[10px] flex-shrink-0 tabular-nums">
                      {formatDuration(entry.metadata.durationMs)}
                    </span>

                    {/* Expand indicator */}
                    {showDetails.has(entry.seq) ? (
                      <ChevronDown className="w-3 h-3 text-[var(--text-muted)] flex-shrink-0" />
                    ) : (
                      <ChevronRight className="w-3 h-3 text-[var(--text-muted)] flex-shrink-0" />
                    )}
                  </button>

                  {/* Expanded Details */}
                  {showDetails.has(entry.seq) && (
                    <div className="px-3 py-2 bg-[var(--bg-primary)] border-t border-[var(--border-color)] text-xs space-y-2">
                      <div className="flex items-center gap-3 text-[var(--text-muted)] text-[11px]">
                        <span className="flex items-center gap-1">
                          <Clock className="w-3 h-3" />
                          {formatTime(entry.timestamp)}
                        </span>
                        {entry.metadata.rowCount !== undefined && (
                          <span>Rows: {entry.metadata.rowCount}</span>
                        )}
                        {entry.metadata.charCount !== undefined && (
                          <span>Chars: {entry.metadata.charCount.toLocaleString()}</span>
                        )}
                        {entry.metadata.wordCount !== undefined && (
                          <span>Words: {entry.metadata.wordCount.toLocaleString()}</span>
                        )}
                      </div>

                      {/* Key Topics */}
                      {entry.metadata.keyTopics &&
                        entry.metadata.keyTopics.length > 0 && (
                          <div className="flex items-center gap-1 flex-wrap">
                            <span className="text-[var(--text-muted)] text-[11px]">
                              Topics:
                            </span>
                            {entry.metadata.keyTopics.map((topic, i) => (
                              <span
                                key={i}
                                className="px-1.5 py-0.5 rounded-full bg-[var(--bg-tertiary)] text-[var(--text-secondary)] text-[10px] border border-[var(--border-color)]"
                              >
                                {topic}
                              </span>
                            ))}
                          </div>
                        )}

                      {/* Self-Correction Details */}
                      {entry.metadata.correctionApplied && (
                        <div className="p-2 bg-[var(--bg-tertiary)] rounded-md border border-[var(--border-color)]">
                          <div className="flex items-center gap-1.5 text-[11px] font-medium text-[var(--text-primary)] mb-1">
                            <RefreshCw className="w-3 h-3 text-amber-500 dark:text-amber-400" />
                            Self-Correction Applied
                          </div>
                          {entry.metadata.intendedState && (
                            <div className="text-[11px] text-[var(--text-muted)]">
                              <span className="font-medium text-[var(--text-secondary)]">Expected:</span>{" "}
                              {entry.metadata.intendedState}
                            </div>
                          )}
                          {entry.metadata.actualState && (
                            <div className="text-[11px] text-[var(--text-muted)]">
                              <span className="font-medium text-[var(--text-secondary)]">Actual:</span>{" "}
                              {entry.metadata.actualState}
                            </div>
                          )}
                        </div>
                      )}

                      {/* Error */}
                      {entry.metadata.errorMessage && (
                        <div className="p-2 bg-red-50 dark:bg-red-900/10 rounded-md border border-red-200 dark:border-red-900/20 text-red-700 dark:text-red-400 text-[11px]">
                          {entry.metadata.errorMessage}
                        </div>
                      )}
                    </div>
                  )}
                </div>
              ))}
            </div>

            {/* Summary Footer */}
            {auditSummary && (
              <div className="border-t border-[var(--border-color)] px-3 py-2 flex items-center justify-between text-[11px] text-[var(--text-muted)]">
                <div className="flex items-center gap-3">
                  <span>
                    <span className="font-medium text-[var(--text-secondary)]">
                      {auditSummary.gatherInfoSteps}
                    </span>{" "}
                    gather
                  </span>
                  <span>
                    <span className="font-medium text-[var(--text-secondary)]">
                      {auditSummary.dataOpSteps}
                    </span>{" "}
                    data ops
                  </span>
                  <span>
                    <span className="font-medium text-[var(--text-secondary)]">
                      {auditSummary.outputSteps}
                    </span>{" "}
                    outputs
                  </span>
                </div>
                <span className="truncate ml-3">
                  {auditSummary.toolsUsed.slice(0, 3).join(", ")}
                  {auditSummary.toolsUsed.length > 3 && ` +${auditSummary.toolsUsed.length - 3}`}
                </span>
              </div>
            )}
          </div>
        )}
      </div>

      {/* â”€â”€ Self-Corrections Summary (if any) â”€â”€ */}
      {selfCorrections.length > 0 && (
        <div className="border border-[var(--border-color)] rounded-lg bg-[var(--bg-secondary)] p-3">
          <div className="flex items-center gap-2 mb-1.5">
            <RefreshCw className="w-3.5 h-3.5 text-amber-500 dark:text-amber-400" />
            <span className="text-xs font-medium text-[var(--text-primary)]">
              Self-Corrections ({selfCorrections.length})
            </span>
          </div>
          <div className="space-y-1">
            {selfCorrections.map((entry) => (
              <div
                key={entry._id}
                className="text-[11px] text-[var(--text-muted)] flex items-start gap-2"
              >
                <AlertTriangle className="w-3 h-3 text-amber-500 dark:text-amber-400 flex-shrink-0 mt-0.5" />
                <span>{entry.description}</span>
              </div>
            ))}
          </div>
        </div>
      )}
    </div>
  );
}

// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•
// AI ANALYSIS BADGE â€” Labels non-deterministic LLM content
// â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•â•

interface AIAnalysisBadgeProps {
  className?: string;
}

/**
 * Badge to visually label AI-generated (non-deterministic) content.
 * Applied to any LLM-generated analysis sections.
 */
export function AIAnalysisBadge({ className }: AIAnalysisBadgeProps) {
  return (
    <span
      className={cn(
        "inline-flex items-center gap-1 px-2 py-0.5 rounded-full",
        "bg-purple-100 text-purple-700 dark:bg-purple-900/20 dark:text-purple-400 text-[10px] font-medium",
        "border border-purple-200 dark:border-purple-900/30",
        className
      )}
      title="This content was generated by AI and is non-deterministic. The raw data and audit log are the source of truth."
    >
      <Zap className="w-3 h-3" />
      AI Analysis
    </span>
  );
}

/**
 * Wrapper component that detects AI Analysis sections in message text
 * and adds the appropriate badge.
 */
export function TraceContentLabeler({
  content,
  className,
}: {
  content: string;
  className?: string;
}) {
  // Detect the TRACE markers in the merged result
  const aiAnalysisMarker = "âš¡ **AI Analysis**";
  const auditTrailMarker = "ğŸ”’ **Execution Trace**";

  const hasAIAnalysis = content.includes(aiAnalysisMarker);
  const hasAuditTrail = content.includes(auditTrailMarker);

  if (!hasAIAnalysis && !hasAuditTrail) {
    return null;
  }

  return (
    <div className={cn("flex items-center gap-2 mt-2", className)}>
      {hasAuditTrail && (
        <span className="inline-flex items-center gap-1 px-2 py-0.5 rounded-full bg-green-100 text-green-700 dark:bg-green-900/20 dark:text-green-400 text-[10px] font-medium border border-green-200 dark:border-green-900/30">
          <Shield className="w-3 h-3" />
          Verified Trace
        </span>
      )}
      {hasAIAnalysis && <AIAnalysisBadge />}
    </div>
  );
}
